tool.command:
input_dir="\$(dirname '$input_data')" &&
input_parent_dir="\$(dirname "\$input_dir")" &&
work_parent_dir="\$(dirname "\$(pwd)")" &&
if [ "\$input_parent_dir" = "\$work_parent_dir" ]; then
    echo "input dir parent \$input_parent_dir == work dir parent \$work_parent_dir";
    echo 'OK' > 1;
else
    echo "input dir parent \$input_parent_dir != work dir parent \$work_parent_dir";
    echo 'ERROR' > 1;
fi
